package com.gwax.aoc2017.day19

sealed class Path
object Vertical : Path()
object Horizontal : Path()
object Corner : Path()
object Empty : Path()
data class Letter(val char: Char) : Path()
fun path(char: Char): Path = when(char) {
    '|' -> Vertical
    '-' -> Horizontal
    '+' -> Corner
    ' ' -> Empty
    in 'A'..'Z' -> Letter(char)
    else -> throw IllegalArgumentException(char.toString())
}

fun List<List<Path>>.get(p: Point): Path =
        if (p.y < 0 || p.x < 0 || p.y >= this.size || p.x > this[0].size)
            Empty
        else
            this[p.y][p.x]

fun buildRoute(input: String): List<List<Path>> {
    var route = input.lines()
            .map {
                it.map { path(it) }.toMutableList()
            }
    var maxX = route.map { it.size }.max() ?: 0
    route.forEach {
        while (it.size < maxX) it.add(Empty)
    }
    return route.map { it.toList() }
}


data class Point(val x: Int, val y: Int) {
    operator fun plus(other: Point) =
        Point(x + other.x, y + other.y)
}

enum class Direction(val dp: Point) {
    N(Point(0, -1)),
    E(Point(1, 0)),
    S(Point(0, 1)),
    W(Point(-1, 0))
}

enum class Status { Active, Done }

data class Packet(var p: Point, var d: Direction, val seen: MutableList<Letter>, val steps: MutableList<Direction>) {
    fun step(route: List<List<Path>>): Status {
        val nextP = p + d.dp
        val nextPath = route.get(nextP)
        var newDir = d
        when (nextPath) {
            is Letter -> seen.add(nextPath)
            is Empty -> return Status.Done
            is Corner -> newDir = when (d) {
                Direction.N, Direction.S -> {
                    val wPath = route.get(nextP + Direction.W.dp)
                    if (wPath is Horizontal || wPath is Letter)
                        Direction.W
                    else
                        Direction.E
                }
                Direction.E, Direction.W -> {
                    val nPath = route.get(nextP + Direction.N.dp)
                    if (nPath is Vertical || nPath is Letter)
                        Direction.N
                    else
                        Direction.S
                }
            }
        }
        steps.add(d)
        p = nextP
        d = newDir
        return Status.Active
    }
}

fun startPacket(route: List<List<Path>>): Packet {
    val x = route[0].indexOf(Vertical)
    return Packet(Point(x, 0), Direction.S, mutableListOf(), mutableListOf(Direction.S))
}

fun main(args: Array<String>) {
    val route = buildRoute(input)
    val packet = startPacket(route)
    do {
        val status = packet.step(route)
    } while (status == Status.Active)
    println(packet.seen.map { it.char }.joinToString(""))
    println(packet.steps.size)
}


